import { Responsive, WidthProvider } from "react-grid-layout";
import { ReactGridLayoutProps, GridItemWrapper, GridItemContent, Root } from "react-grid-layout";

const ResponsiveGridLayout = WidthProvider(Responsive);


const layout = [
  { i: "blue-eyes-dragon", x: 0, y: 0, w: 1, h: 1 },
  { i: "dark-magician", x: 1, y: 0, w: 1, h: 1 },
  { i: "kuriboh", x: 2, y: 0, w: 1, h: 1 },
  { i: "spell-caster", x: 3, y: 0, w: 1, h: 1 },
  { i: "summoned-skull", x: 4, y: 0, w: 1, h: 1 }
];

function Songs({song, playlists, addToPlaylists, removeFromPlaylists, songitem, handleRatingSubmit}) {






